#include<iostream>
using namespace std;

class Parent{
    private:
        int net_worth =0;
    public:
        string family_name , family_head;
        int number_of_members;

        void setData(int worth,string familyName,string familyHead,int member_cout ){
            net_worth = worth;
            family_name = familyName;
            family_head=familyHead;
            number_of_members=member_cout;
        }
        int showNetWorth(){
            return net_worth;
        }
       
        int getFamilyInfo(){
            cout << "I'm from " << family_name
            <<" family ,and my family head is " << family_head << ".our Family's Net worth is about"
            << showNetWorth() << endl;

        }
};
//syntax  for declaring the derived class:
//class derived -class-name : accessibility-mode base-class-name
//by default : derived class uses private accessibility mode
//if derived class uses private accessibility mode then public data & member function of the baase class will become private
